import  yamlimport osdef read(yaml_name=None,length=None,**kwargs):    # 获取当前脚本所在文件夹路径将    当前文件夹     ..    curPath = os.path.abspath(os.path.join(os.path.dirname(__file__), os.pardir))    # 获取yaml文件路径    yamlPath = os.path.join(curPath, "resource/"+yaml_name)    # open方法打开直接读出来    f = open(yamlPath, 'r', encoding='utf-8')    cfg = f.read()    d = yaml.load(cfg)  # 用load方法转字典    #print(d.__class__)  list列表    value1 =d[length].get("element_info")    value2 = d[length].get("find_type")    return value1 ,value2print(read(yaml_name="abc.yaml",length=3))